class Loader():
    def __init__(self, parent=None):
        # QtGui.QMainWindow.__init__(parent)
        self.AMDock = parent

    def copy_target(self):
        try:
            shutil.copy('%s' % self.AMDock.target.path, self.t_input)
            self.AMDock.target.input = self.t_input
        except:
            nowdir = QtGui.QMessageBox.critical(self.AMDock, 'Error', 'The working directory was not found or '
                                                              'it does not have permission for writing.'
                                                              '\nPlease reset the program.',
                                                QtGui.QMessageBox.Retry | QtGui.QMessageBox.Cancel)
            if nowdir == QtGui.QMessageBox.Retry:
                self.copy_target()
            else:
                return False
        return True

    def copy_offtarget(self):
        try:
            shutil.copy('%s' % self.AMDock.offtarget.path, self.o_input)
            self.AMDock.offtarget.input = self.o_input
        except:
            nowdir = QtGui.QMessageBox.critical(self.AMDock, 'Error', 'The working directory was not found or '
                                                              'it does not have permission for writing.'
                                                              '\nPlease reset the program.',
                                                QtGui.QMessageBox.Retry | QtGui.QMessageBox.Cancel)
            if nowdir == QtGui.QMessageBox.Retry:
                self.copy_offtarget()
            else:
                return False
        return True

    def copy_ligand(self):
        try:
            shutil.copy('%s' % self.AMDock.ligand.path, self.l_input)
            self.AMDock.ligand.input = self.l_input
        except:
            nowdir = QtGui.QMessageBox.critical(self.AMDock, 'Error', 'The working directory was not found or '
                                                              'it does not have permission for writing.'
                                                              '\nPlease reset the program.',
                                                QtGui.QMessageBox.Retry | QtGui.QMessageBox.Cancel)
            if nowdir == QtGui.QMessageBox.Retry:
                self.copy_ligand()
            else:
                return False
        return True

    def create_project_function(self):
        self.AMDock.project.get_info(self.AMDock.program_body.project_text.text())
        try:
            os.chdir(self.AMDock.project.location)
        except:
            wdir_warning(self.AMDock)
            return
        if os.path.exists(self.AMDock.project.name):
            rt = wdir3_warning(self.AMDock)
            if rt == QtGui.QMessageBox.Yes:
                try:
                    os.rename(self.AMDock.project.name, self.AMDock.project.name + '_old_' + "%s-%s-%s-%s-%s-%s"
                      % time.localtime(os.path.getmtime(self.AMDock.project.name))[0:6])
                except:
                    msg = QtGui.QMessageBox.critical(self.AMDock, 'Error',
                                                     'The directory could not be renamed. Please check that you have '
                                                     'writing rights.', QtGui.QMessageBox.Ok)
                    return
            else:
                return
        try:
            os.mkdir(self.AMDock.project.name)
            os.chdir(self.AMDock.project.WDIR)
            os.mkdir('input')
            os.mkdir('results')
            # progress(self.AMDock.program_body, 0, 1, 2, finish=True, mess='Working Directory Definition...')
            self.AMDock.statusbar.showMessage("Create project: %s" % self.AMDock.project.WDIR, 3000)
            self.AMDock.output2file.file_header( self.AMDock.project.output)
            self.AMDock.output2file.out2file('>> PROJECT_NAME: %s\n' % self.AMDock.project.name)
            self.AMDock.output2file.out2file('>> WORKING_DIRECTORY: %s\n' % self.AMDock.project.WDIR)
            return True
        except:
            wdir_warning(self.AMDock)
            return

    def project_location(self):
        data_file = QtGui.QFileDialog()
        data_file.setFileMode(QtGui.QFileDialog.DirectoryOnly)
        if data_file.exec_():
            # self.AMDock.program_body.project_text.setDisabled(True)
            filenames = data_file.selectedFiles()
            return filenames

